mapper.select:
SELECT T1.*
FROM  T1
WHERE T1.ID = #{value}

mapper.insert:
INSERT INTO 
(
    REG_DT
    , REG_ID

    , LENGTH
    , ORIGINAL_FILE_NAME
    , FILE_EXTENSION
    , SAVE_FILE_NAME
    , UPLOAD_URL
    , LOCAL_PATH
)
VALUES
(
    NOW()
    , #{loginUserId}

    , #{length}
    , #{originalFileName}
    , #{fileExtension}
    , #{saveFileName}
    , #{uploadUrl}
    , #{localPath}
)

mapper.update:
UPDATE 
SET (
    UPD_DT = NOW()
    , UPD_ID = #{loginUserId}
    , LENGTH = #{length}
    , ORIGINAL_FILE_NAME = #{originalFileName}
    , FILE_EXTENSION = #{fileExtension}
    , SAVE_FILE_NAME = #{saveFileName}
    , UPLOAD_URL = #{uploadUrl}
    , LOCAL_PATH = #{localPath}
)
WHERE ID = #{id}
